function validatePassword($password) {
// Configuration des critères de validation
$minLength = 8;
$minLowercase = 1;
$minUppercase = 1;
$minNumbers = 1;
$minSymbols = 1;

// Expression régulière pour vérifier la présence de minuscules, majuscules, chiffres et symboles
$lowercaseRegex = '/[a-z]/';
$uppercaseRegex = '/[A-Z]/';
$numbersRegex = '/[0-9]/';
$symbolsRegex = '/[^a-zA-Z0-9]/';

// Vérification de la longueur minimale
if (strlen($password) < $minLength) {return 'Le mot de passe est trop court (minimum ' . $minLength . ' caractères).';}

// Vérification de la présence de minuscules
if (!preg_match($lowercaseRegex, $password)) {return 'Le mot de passe doit contenir au moins ' . $minLowercase . ' lettre(s) minuscule(s).';}

// Vérification de la présence de majuscules
if (!preg_match($uppercaseRegex, $password)) {return 'Le mot de passe doit contenir au moins ' . $minUppercase . ' lettre(s) majuscule(s).';}

// Vérification de la présence de chiffres
if (!preg_match($numbersRegex, $password)) {return 'Le mot de passe doit contenir au moins ' . $minNumbers . ' chiffre(s).';}

// Vérification de la présence de symboles
if (!preg_match($symbolsRegex, $password)) {return 'Le mot de passe doit contenir au moins ' . $minSymbols . ' symbole(s).';}

// Le mot de passe a passé toutes les vérifications, il est considéré comme fort
return 'Mot de passe fort.';
}
